	, district_name=#{districtName}


	, price=#{price}


	, discount_price=#{discountPrice}


	, title=#{title}


	, local_title=#{localTitle}


	, description=#{description}


	, address=#{address}


	, file_id=#{fileId}


	, cover_picture=#{coverPicture}


	, room_count=#{roomCount}


	, grade=#{grade}


