	, source_type_key=#{sourceTypeKey}


	, source_type=#{sourceType}


	, lease_model_key=#{leaseModelKey}


	, lease_model=#{leaseModel}


	, bathroom_type_key=#{bathroomTypeKey}


	, bathroom_type=#{bathroomType}


